            arrayToSort,
            orderBy,
            sortingField,
            isNonAggregated: chartSubType === ln.PieNoAggregation ||
                chartSubType === ln.BarAndLineNoAggregation ||
                chartSubType === ln.BarAndLineSplitByNoAggregation,
            sortByCategory,
            forceAscendingOrder,
            chartSubType,
            preferLabel,
            fieldList,
