import { IGraphicsSettings, ILabelSettings, ISpriteSettings, Pattern } from "@amcharts/amcharts5";
import { AmChartsColor } from "../../types";
export interface MappedLineSymbol {
    stroke: AmChartsColor;
    strokeWidth: IGraphicsSettings["strokeWidth"];
    strokeDasharray: number[] | number;
    strokeLinecap: "butt" | "round" | "square";
    strokeOpacity: number;
}
export interface MappedFillSymbol {
    pattern?: Pattern;
    fill: AmChartsColor;
    fillOpacity: number;
    outline: MappedLineSymbol;
}
export interface MappedMarkerSymbol {
    fill: AmChartsColor;
    fillOpacity: number;
    path: string;
    outline: MappedLineSymbol;
    xOffset: ISpriteSettings["dx"];
    yOffset: ISpriteSettings["dy"];
    angle: ISpriteSettings["rotation"];
}
export interface MappedTextSymbol {
    fill: AmChartsColor;
    fontFamily: ILabelSettings["fontFamily"];
    fontSize: number;
    fontWeight: ILabelSettings["fontWeight"];
    text: ILabelSettings["text"];
    align: ILabelSettings["centerX"];
    valign: ILabelSettings["centerY"];
    horizontalCenter: ILabelSettings["centerX"];
    verticalCenter: ILabelSettings["centerY"];
    textDecoration: ILabelSettings["textDecoration"];
    angle: ILabelSettings["rotation"];
    xOffset: ILabelSettings["dx"] | string;
    yOffset: ILabelSettings["dy"] | string;
    backgroundFill: AmChartsColor;
    borderWidth: IGraphicsSettings["strokeWidth"];
    borderFill: AmChartsColor;
    haloColor: AmChartsColor;
    rtl: ILabelSettings["direction"];
    wrap: ILabelSettings["oversizedBehavior"];
    maxWidth: ILabelSettings["maxWidth"];
}
