import type { WebChartBarChartSeries, WebChartBoxPlotSeries, WebChartHistogramSeries, WebChartLineChartSeries, WebChartPieChartSeries, WebChartScatterPlotSeries, WebChartRadarChartSeries, IField, WebChart } from "@arcgis/charts-spec";
import type { CommonStrings, QueryObject } from "@arcgis/charts-shared-utils";
