/**
 * Former interface describing the property `configFields` from an inline data source.
 * Is an object which dictate which data-item field should be used for defining a property of the rendered element (line, point, bars, etc). Used in inline data source mode only.
 */
export interface WebChartConfigFields {
    fillColor?: string;
    fillOpacity?: string;
    fillStyle?: string;
    lineColor?: string;
    lineOpacity?: string;
    lineWidth?: string;
    lineStyle?: string;
    markerSize?: string;
    outlineColor?: string;
    outlineOpacity?: string;
    outlineWidth?: string;
    outlineStyle?: string;
    displayLabel?: string;
}
