import type { RESTRendererType, WebChart, ICodedValue, SupportedLayer } from "@arcgis/charts-spec";
import type MapView from "@arcgis/core/views/MapView";
import type SceneView from "@arcgis/core/views/SceneView";
import type FeatureLayerView from "@arcgis/core/views/layers/FeatureLayerView";
export interface QueryObjectProps {
    chartConfig: WebChart;
    layer?: FeatureLayerView | SupportedLayer;
    view?: MapView | SceneView;
}
export interface RendererInfo {
    rendererType?: RESTRendererType;
    rendererField?: string;
    rendererNormalizationField?: string;
}
export interface ServerRestInfo {
    currentVersion?: string;
    fullVersion?: string;
    owningSystemUrl?: string;
    owningTenant?: string;
    authInfo?: {
        isTokenBasedSecurity: boolean;
        tokenServicesUrl: string;
    };
}
/**
 * Store information about coded-value domains in the subtype
 */
export interface ICodedValueDomain {
    fieldName: string;
    name: string;
    codedValues?: ICodedValue[];
}
/**
 * Store information about featureType
 */
export interface IFeatureType {
    id: number | string;
    name: string;
    domains?: ICodedValueDomain[];
}
/**
 * Store information about subtypes from the feature layer, if any, with:
 * - either the couple typeIdField + types
 * - or the couple subtypeField + subtypes
 *  representing the field name and information associating a code with a name
 */
export interface SubtypeInfo {
    typeIdField?: string;
    types?: IFeatureType[];
    subtypeField?: string;
    subtypes?: IFeatureType[];
}
